import React from "react";
import API from "./utils/API";

import Header from "./Header";
import HiddenWord from "./HiddenWord";
import Alphabet from "./Alphabet";
import ScoreBoard from "./ScoreBoard";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      difficulty: 3,
      guessedletters: [],
      guesses: 6
    };
  }

  componentDidMount() {
    this.getWord(this.state.difficulty);
  }

  getWord(difficulty) {
    API.getWords(difficulty)
      .then(response => {
        let oneWordArray = response.data.split("");
        this.setState({
          word: oneWordArray
        });
      })
      .catch(function(error) {
        console.log(error);
      })
      .then(function() {
        // always executed
      });
  }

  checkUserScore = letter => {
    if (this.state.word.includes(letter)) {
      this.setState({
        guesses: 1,
        guessedletters: [...this.state.guessedletters, letter]
      });
    } else {
      this.setState({
        guesses: 0,
        guessedletters: [...this.state.guessedletters, letter]
      });
    }
  };

  render() {
    return (
      <div>
        <Header />
        {this.state.word ? (
          <HiddenWord
            word={this.state.word}
            guessedLetters={this.state.guessedletters}
          />
        ) : (
          ""
        )}
        <Alphabet onClick={this.checkUserScore} />
        <ScoreBoard score={this.state.guesses} />
      </div>
    );
  }
}

export default App;
