import React from "react";

const HiddenWord = props => {
  console.log(props);
  return (
    <ul className="horizontal-ul">
      {props.word.map((letter, i) => {
        if (props.endGame === "win") {
          return (
            <li className={"hidden-word-" + props.win} key={i}>
              {letter}
            </li>
          );
        } else if (props.endGame === "lose") {
          return (
            <li className={"hidden-word-" + props.win} key={i}>
              {letter}
            </li>
          );
        } else {
          return props.guessedLetters.includes(letter) ? (
            <li className="hidden-word" key={i}>
              {letter}
            </li>
          ) : (
            <li key={i}>_</li>
          );
        }
      })}
    </ul>
  );
};

export default HiddenWord;
