import React from "react";

const ScoreBoard = props => {
  console.log(props);
  return (
    <div>
      <p>Scoreboard:</p>
      <p>
        {props.userName || "You"}: <span>{props.wins}</span>
      </p>
      <p>
        Computer: <span>{props.losses}</span>
      </p>
      <p>
        Number of guesses remaining: <span>{props.guesses}</span>
      </p>
    </div>
  );
};

export default ScoreBoard;
