import React from "react";
import API from "./utils/API";

import Header from "./Header";
import HiddenWord from "./HiddenWord";
import Alphabet from "./Alphabet";
import ScoreBoard from "./ScoreBoard";
import Result from "./Result";
import DifficultyLevel from "./DifficultyLevel";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      difficulty: 3,
      wins: 0,
      losses: 0
    };
  }

  componentDidMount() {
    this.getWord(this.state.difficulty);

    document.addEventListener("keydown", this.checkLetter, false);
  }

  componentWillUnmount() {
    document.removeEventListener("keydown", this.checkLetter, false);
  }

  getWord(difficulty) {
    API.getWords(difficulty)
      .then(response => {
        let oneWordArray = response.data.split("");
        this.setState({
          word: oneWordArray,
          guessedletters: [],
          guesses: 6,
          endGame: null,
          difficulty: difficulty
        });
      })
      .catch(function(error) {
        console.log(error);
      })
      .then(function() {
        // always executed
      });
  }

  checkLetter = (event, letter) => {
    if (event.keyCode === 27) {
      console.log("esc");
    }
    if (this.state.guessedletters.includes(letter)) return;

    let incrementor = 0;
    if (!this.state.word.includes(letter)) incrementor = -1;
    this.setState(
      prevState => {
        return {
          guesses: prevState.guesses + incrementor,
          guessedletters: [...this.state.guessedletters, letter]
        };
      },
      () => {
        this.checkWinningCondition();
      }
    );
  };

  checkWinningCondition = () => {
    let removeDublicates = this.state.word.filter((item, index) => {
      return this.state.word.indexOf(item) >= index;
    });

    let removeWrongLetters = this.state.guessedletters.filter(item => {
      return this.state.word.includes(item);
    });

    if (
      removeWrongLetters.sort().toString() ===
        removeDublicates.sort().toString() &&
      this.state.guesses > 0
    ) {
      this.setState(prevState => {
        return {
          wins: prevState.wins + 1,
          endGame: "win"
        };
      });
    } else if (this.state.guesses === 0) {
      this.setState(prevState => {
        return {
          losses: prevState.losses + 1,
          endGame: "lose"
        };
      });
    }
  };

  restartGame = () => {
    this.getWord(this.state.difficulty);
  };

  changeDifficultyLevel = e => {
    this.getWord(e.target.value);
  };

  render() {
    return (
      <>
        <Header />

        <ScoreBoard
          guesses={this.state.guesses}
          wins={this.state.wins}
          losses={this.state.losses}
        />

        <DifficultyLevel onChange={this.changeDifficultyLevel} />

        {this.state.word ? (
          <HiddenWord
            word={this.state.word}
            guessedLetters={this.state.guessedletters}
            guesses={this.state.guesses}
            endGame={this.state.endGame}
          />
        ) : null}

        {this.state.guesses > 0 ? (
          <Alphabet
            onClick={this.checkLetter}
            guessedLetters={this.state.guessedletters}
            word={this.state.word}
          />
        ) : null}

        {this.state.endGame ? (
          <Result endGame={this.state.endGame} onClick={this.restartGame} />
        ) : null}
      </>
    );
  }
}

export default App;
