import React from "react";
import API from "./utils/API";

import Header from "./Header";
import HiddenWord from "./HiddenWord";
import Alphabet from "./Alphabet";
import ScoreBoard from "./ScoreBoard";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      difficulty: 3,
      guessedletters: [],
      guesses: 3
    };
  }

  componentDidMount() {
    this.getWord(this.state.difficulty);
  }

  getWord(difficulty) {
    API.getWords(difficulty)
      .then(response => {
        let oneWordArray = response.data.split("");
        this.setState({
          word: oneWordArray
        });
      })
      .catch(function(error) {
        console.log(error);
      })
      .then(function() {
        // always executed
      });
  }

  checkUserScore = letter => {
    if (this.state.guessedletters.includes(letter)) return;

    let incrementor = 0;
    this.state.word.includes(letter) ? (incrementor = 1) : (incrementor = -1);
    this.setState(prevState => {
      return {
        guesses: prevState.guesses + incrementor,
        guessedletters: [...this.state.guessedletters, letter]
      };
    });
  };

  render() {
    return (
      <div>
        <Header />
        {this.state.word ? (
          <HiddenWord
            word={this.state.word}
            guessedLetters={this.state.guessedletters}
            guesses={this.state.guesses}
          />
        ) : null}
        {this.state.guesses > 0 ? (
          <Alphabet
            onClick={this.checkUserScore}
            guessedLetters={this.state.guessedletters}
            word={this.state.word}
          />
        ) : null}

        <ScoreBoard score={this.state.guesses} />
      </div>
    );
  }
}

export default App;
