import React from "react";
import API from "./utils/API";

import Header from "./Header";
import HiddenWord from "./HiddenWord";
import Alphabet from "./Alphabet";
import ScoreBoard from "./ScoreBoard";
import Result from "./Result";
import DifficultyLevel from "./DifficultyLevel";
import UserName from "./UserName";
import LeaderBoard from "./LeaderBoard";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      difficulty: 3,
      wins: 0,
      losses: 0,
      leaderBoard: {}
    };
  }

  componentDidMount() {
    this.getWord(this.state.difficulty);

    this.addEventListener();
  }

  componentDidUpdate() {
    console.log(this.state.leaderBoard);
  }
  componentWillUnmount() {
    this.removeEventListener();
  }

  addEventListener = () => {
    document.addEventListener("keydown", this.handleKeyDown);
  };

  removeEventListener = () => {
    document.removeEventListener("keydown", this.handleKeyDown);
  };

  getWord(difficulty) {
    API.getWords(difficulty)
      .then(response => {
        let oneWordArray = response.data.split("");
        this.setState({
          word: oneWordArray,
          guessedletters: [],
          guesses: 6,
          endGame: null,
          difficulty: difficulty,
          name: null
        });
      })
      .catch(function(error) {
        console.log(error);
      })
      .then(function() {
        // always executed
      });
  }

  checkLetter = letter => {
    if (this.state.guessedletters.includes(letter)) return;

    let incrementor = 0;
    if (!this.state.word.includes(letter)) incrementor = -1;
    this.setState(
      prevState => {
        return {
          guesses: prevState.guesses + incrementor,
          guessedletters: [...this.state.guessedletters, letter]
        };
      },
      () => {
        this.checkWinningCondition();
      }
    );
  };

  handleKeyDown = event => {
    const letterNumber = /^[0-9a-zA-Z]+$/;

    if (String.fromCharCode(event.keyCode).match(letterNumber)) {
      this.checkLetter(String.fromCharCode(event.keyCode).toLowerCase());
    } else {
      this.setState({ keydownError: "Please, use letters and numbers only!" });
    }
  };

  checkWinningCondition = () => {
    let removeDublicates = this.state.word.filter((item, index) => {
      return this.state.word.indexOf(item) >= index;
    });

    let removeWrongLetters = this.state.guessedletters.filter(item => {
      return this.state.word.includes(item);
    });

    if (
      removeWrongLetters.sort().toString() ===
        removeDublicates.sort().toString() &&
      this.state.guesses > 0
    ) {
      var newState = Object.assign({}, this.state.leaderBoard);
      console.log(newState);
      if (newState[this.state.userName]) {
        newState[this.state.userName] += 1;
      } else {
        newState[this.state.userName] = 1;
      }

      this.setState(prevState => {
        return {
          wins: prevState.wins + 1,
          endGame: "win",
          leaderBoard: newState
        };
      });
    } else if (this.state.guesses === 0) {
      this.setState(prevState => {
        return {
          losses: prevState.losses + 1,
          endGame: "lose"
        };
      });
    }
  };

  restartGame = () => {
    this.getWord(this.state.difficulty);
  };

  changeDifficultyLevel = e => {
    this.getWord(e.target.value);
  };

  addNewUser = name => {
    this.setState({
      userName: name
    });
  };

  render() {
    return (
      <>
        <Header />

        <ScoreBoard
          guesses={this.state.guesses}
          wins={this.state.wins}
          losses={this.state.losses}
          userName={this.state.userName}
        />

        <UserName
          onClick={this.addNewUser}
          onFocus={this.removeEventListener}
          onBlur={this.addEventListener}
        />

        <DifficultyLevel onChange={this.changeDifficultyLevel} />

        {this.state.word ? (
          <HiddenWord
            word={this.state.word}
            guessedLetters={this.state.guessedletters}
            guesses={this.state.guesses}
            endGame={this.state.endGame}
          />
        ) : null}

        {this.state.guesses > 0 ? (
          <Alphabet
            onClick={this.checkLetter}
            guessedLetters={this.state.guessedletters}
            word={this.state.word}
            keydownError={
              this.state.keydownError ? this.state.keydownError : ""
            }
          />
        ) : null}

        {this.state.endGame ? (
          <Result endGame={this.state.endGame} onClick={this.restartGame} />
        ) : null}

        <LeaderBoard />
      </>
    );
  }
}

export default App;
