import React from "react";
import API from "./utils/API";

import HiddenWord from "./HiddenWord";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      difficulty: 3,
      word: ""
    };
  }

  componentDidMount() {
    this.getWord(this.state.difficulty);
  }

  getWord(difficulty) {
    API.getWords(difficulty)
      .then(function(response) {
        this.setState({
          word: response.data
        });
      })
      .catch(function(error) {
        console.log(error);
      })
      .then(function() {
        // always executed
      });
  }

  render() {
    return (
      <div>
        Hey
        <HiddenWord word={this.state.word} />
      </div>
    );
  }
}

export default App;
