import React from "react";
import API from "./utils/API";

import HiddenWord from "./HiddenWord";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      difficulty: 3
    };
  }

  componentDidMount() {}

  getWord(difficulty) {
    return API.getWords(difficulty)
      .then(function(response) {
        return response.data;
      })
      .catch(function(error) {
        console.log(error);
      })
      .then(function() {
        // always executed
      });
  }

  render() {
    return (
      <div>
        Hey
        <HiddenWord word={this.getWord(this.state.difficulty)} />
      </div>
    );
  }
}

export default App;
