import React from "react";

const HiddenWord = props => {
  console.log(props);
  return (
    <ul className="horizontal-ul">
      {props.word.map((letter, i) => {
        if (props.guesses && !props.win) {
          return props.guessedLetters.includes(letter) ? (
            <li className="hidden-word" key={i}>
              {letter}
            </li>
          ) : (
            <li key={i}>_</li>
          );
        } else if (props.guesses && props.win) {
          return (
            <li className="hidden-word-win" key={i}>
              {letter}
            </li>
          );
        } else {
          return (
            <li className="hidden-word-lose" key={i}>
              {letter}
            </li>
          );
        }
      })}
    </ul>
  );
};

export default HiddenWord;
